import axios from "axios"

export const getCoins = ()=>{
    return axios.get(`https://api.coingecko.com/api/v3/coins/list`)   
}

export const getHistoricalData = (coin,date)=>{
    return axios.get(`https://api.coingecko.com/api/v3/coins/${coin}/history?date=${date}`)
}

export const getCurrentData = (coin)=>{
    return axios.get(`https://api.coingecko.com/api/v3/coins/${coin}?tickers=false&market_data=true&community_data=false&developer_data=false&sparkline=false`)
}
