import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { getCoins, getCurrencies } from './api'
import Select from 'react-select';
import { getCalculatedPrice } from '../home/api';
import { ToastContainer, toast } from 'react-toastify';



const Converter = () => {

    const [currencies, setCurrencies] = useState([])
    const [coins, setCoins] = useState([])
    const [selectedCoin, setSelectedCoin] = useState("")
    const [selectedCurrency, setSelectedCurrency] = useState("")
    const [calculated, setCalculated] = useState(null)

    useEffect(() => {
        getCurrencies().then((res) => {
            setCurrencies(res.data?.map((a) => {
                return {
                    value: a,
                    label: a
                }
            }))
        }).catch((e) => {
            console.log("Get curr failed")
        })

        getCoins().then((res) => {
            setCoins(res.data?.map((a) => {
                return {
                    value: a.id,
                    label: a.id
                }
            }).filter((b)=>!b.label.includes("-")))
        }).catch((e) => {
            console.log("GEt coins failes")
        })
    }, [])

    const handleCoinChange = (selectedOption) => {
        setSelectedCoin(selectedOption)
    }

    const handleCurrencyChange = (selectedOption) => {
        setSelectedCurrency(selectedOption)
    }

    const handleCalculate = ()=>{
        if(selectedCoin===""){
        return toast.warn("Please Select a Coin")
    }
    else if(selectedCurrency===""){
        return toast.warn("Please Select a Currency")
    }
        getCalculatedPrice(selectedCoin?.value,selectedCurrency?.value).then((res)=>{
            var c = Object.values(res.data)[0]
            var d = Object.values(c)[0]
            setCalculated(d)
        })
    }

    // console.log(selectedCoin, selectedCurrency)

    return (
        <div className="Converter row">
            <div className="Converter__label col-md-6">
                <h1 className="text-purple">Crypto â†’ Currency Calculator</h1>
                <h4 className="text-grey">Get the current price of any cryptocurrencies in any other supported currencies that you need</h4>
            </div>
            <div className="Converter__calculator col-md-6">
                <div className="mb-3">
                    <Select
                        value={selectedCoin}
                        onChange={(e) => handleCoinChange(e)}
                        options={coins}
                    />
                </div>
                <div>
                    <Select
                        value={selectedCurrency}
                        onChange={(e) => handleCurrencyChange(e)}
                        options={currencies}
                    />
                </div>
                <div className="my-3">
                    <button  className="btn-primary" type="button" onClick={()=>handleCalculate()}>Calculate</button>
                </div>
                <div className="my-1">
                    {calculated && <h4>{calculated}</h4>}
                </div>
                <ToastContainer/>
            </div>
        </div>
    )
}

export default Converter
