import React from 'react'
import { useState } from 'react';
import { useEffect } from 'react';
import Slider from "react-slick";
import HorizontalCard from '../../../../reuseable/HorizontalCard/HorizontalCard';
import { getTrendingCoins } from '../../api';


const Trending = () => {

    const [coins, setCoins] = useState([])

    useEffect(() => {
        getTrendingCoins().then((res) => {
            setCoins(res.data?.coins)
        }).catch((e) => {
            console.log(e)
        })
    }, [])

    console.log(coins)

    const settings = {
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        autoplay: true,
        speed: 3000,
        autoplaySpeed: 3000,
        cssEase: "linear",
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 1,
                    infinite: true,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    initialSlide: 2
                }
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            }
        ]
    };
    return (
        <div className="my-2">
            <h6 className="mb-3">Trending Coins</h6>
            <Slider {...settings}>
                {coins.length>0 &&coins.map((a) => {
                    return (
                        <div>
                            <HorizontalCard coin={a}/>
                        </div>
                    )
                })}
            </Slider>
        </div>
    );

}

export default Trending
