import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { getCoins, getCurrentData, getHistoricalData } from './api'
import Select from 'react-select';
import { convertDate } from "../../helpers/convertDate"
import { ToastContainer, toast } from 'react-toastify';


const Historical = () => {
    const [coins, setCoins] = useState([])
    const [selectedCoin, setSelectedCoin] = useState("")
    const [date, setDate] = useState("")
    const [calculated, setCalculated] = useState(null)
    const [current, setCurrent] = useState(null)



    useEffect(() => {
        getCoins().then((res) => {
            setCoins(res.data?.map((a) => {
                return {
                    value: a.id,
                    label: a.id
                }
            }).filter((b)=>!b.label.includes("-")))
        }).catch((e) => {
            console.log("GEt coins failes")
        })
    }, [])

    const handleCoinChange = (selectedOption) => {
        setSelectedCoin(selectedOption)
    }

    const handleChange = (e) => {
        setDate(e.target.value)
    }

    const handleCalculate = () => {
        if (selectedCoin === "") {
            return toast.warn("Please Select a Coin")
        }
        else if (date === "") {
            return toast.warn("Please Select a Date")
        }
        var dateformat = convertDate(date)
        getHistoricalData(selectedCoin?.value, dateformat).then((res) => {
            setCalculated(res.data)
        })
        getCurrentData(selectedCoin?.value).then((res) => {
            setCurrent(res.data)
        })
    }


    console.log(calculated)


    return (
        <div className="Historical row">
            <div className="Historical__label col-md-6">
                <h1 className="text-purple">Backtest</h1>
                <h4 className="text-grey">Know how a cryptocurrency performed on a particular day and compare it with today. Isn't that amazing?!</h4>
            </div>
            <div className="Historical__calculator col-md-6">
                <div className="mb-3">
                    <Select
                        value={selectedCoin}
                        onChange={(e) => handleCoinChange(e)}
                        options={coins}
                    />
                </div>
                <div>
                    <input placeholder="dd-mm-yyyy" type="date" min="2005-01-01" max={new Date().toISOString().split("T")[0]} name="date" onChange={(e) => handleChange(e)} />
                </div>
                <div className="my-3">
                    <button className="btn-primary" type="button" onClick={() => handleCalculate()}>Show Me!</button>
                </div>
                <div className="Historical__calculator__stats my-1">
                    {calculated && current && <div>
                        <table>
                            <thead>
                                <tr>
                                    <th></th>
                                    {date!==""&&<th>{convertDate(date)}</th>}
                                    <th>Current</th>
                                    <th>Difference</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>Price</td>
                                    <td className="text-yellow">{calculated?.market_data?.current_price?.cad || "N/A"}$</td>
                                    <td className="text-purple">{current?.market_data?.current_price?.cad || "N/A"}$</td>
                                    <td className={current?.market_data?.current_price?.cad-calculated?.market_data?.current_price?.cad >0 ?"text-green":"text-maroon"}>{current?.market_data?.current_price?.cad-calculated?.market_data?.current_price?.cad || "N/A"}$</td>
                                </tr>
                                <tr>
                                    <td>Market Cap</td>
                                    <td className="text-yellow">{calculated?.market_data?.market_cap?.cad  || "N/A"}$</td>
                                    <td className="text-purple">{current?.market_data?.market_cap?.cad  || "N/A"}$</td>
                                    <td className={current?.market_data?.market_cap?.cad-calculated?.market_data?.market_cap?.cad >0 ?"text-green":"text-maroon"}>{current?.market_data?.market_cap?.cad-calculated?.market_data?.market_cap?.cad  || "N/A"}$</td>
                                </tr>
                                <tr>
                                    <td>Volume</td>
                                    <td className="text-yellow">{calculated?.market_data?.total_volume.cad  || "N/A"}$</td>
                                    <td className="text-purple">{current?.market_data?.total_volume?.cad  || "N/A"}$</td>
                                    <td className={current?.market_data?.total_volume?.cad-calculated?.market_data?.total_volume?.cad >0 ?"text-green":"text-maroon"}>{current?.market_data?.total_volume?.cad-calculated?.market_data?.total_volume.cad  || "N/A"}$</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>}
                </div>
                <ToastContainer />
            </div>
        </div>
    )
}

export default Historical
